import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';

i18n
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    fallbackLng: 'en',
    debug: true,
    interpolation: {
      escapeValue: false,
    },
    resources: {
      en: {
        translation: {
          missionTitle: "WHY WE EXIST",
          missionSub: "Empowering Teachers. Uplifting Learners. Celebrating Neurodiversity.",
          // ...add more keys
        },
      },
      ar: {
        translation: {
          missionTitle: "لماذا نحن موجودون",
          missionSub: "تمكين المعلمين. رفع مكانة المتعلمين. الاحتفال بالتنوع العصبي.",
          // ...same keys in Arabic
        },
      },
    },
  });

export default i18n;
