import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';

const Contact = () => {
  const { t, i18n } = useTranslation();
  const isRTL = i18n.language === 'ar';
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    const honeypot = e.target.elements._gotcha?.value;
    if (honeypot) {
      e.preventDefault(); // Bot detected
      return;
    }
    setSubmitted(true);
  };

  return (
    <div
      className={`container ${isRTL ? 'arabic-font' : ''}`}
      style={{ direction: isRTL ? 'rtl' : 'ltr' }}
    >
      <h1 className="storybook-heading">{t('contactTitle')}</h1>
      <p style={{ maxWidth: '650px' }}>{t('contactIntro')}</p>

      {submitted && (
        <div
          style={{
            backgroundColor: '#35DAAD',
            color: 'white',
            padding: '1rem',
            textAlign: 'center',
            borderRadius: '8px',
            marginTop: '1rem',
            fontWeight: '600',
          }}
        >
          {t('contactSuccess')}
        </div>
      )}

      <form
        action="https://formspree.io/f/xnnvbwwd"
        method="POST"
        onSubmit={handleSubmit}
        style={{
          background: 'white',
          padding: '2rem',
          borderRadius: '16px',
          boxShadow: '0 4px 10px rgba(0,0,0,0.05)',
          marginTop: '2rem',
          maxWidth: '650px',
          marginInline: 'auto',
        }}
      >
        {/* Honeypot */}
        <input type="text" name="_gotcha" style={{ display: 'none' }} />

        <div className="form-floating">
          <input type="text" name="name" required placeholder={t('contactName')} />
          <label>{t('contactName')}</label>
        </div>

        <div className="form-floating">
          <input type="email" name="email" required placeholder={t('contactEmail')} />
          <label>{t('contactEmail')}</label>
        </div>

        <div className="form-floating">
          <textarea
            name="message"
            rows="5"
            required
            placeholder={t('contactMessage')}
          />
          <label>{t('contactMessage')}</label>
        </div>

        <button
          type="submit"
          className="btn"
          style={{
            marginTop: '1.5rem',
            backgroundColor: '#f78ca2',
            border: 'none',
            fontWeight: '600',
          }}
        >
          {t('contactSend')}
        </button>
      </form>

      <div
        className="card"
        style={{
          marginTop: '3rem',
          maxWidth: '650px',
          marginInline: 'auto',
        }}
      >
        <p>
          <strong>{t('emailLabel')}</strong>{' '}
          <a href="mailto:mydearteacherletter@gmail.com">
            mydearteacherletter@gmail.com
          </a>
        </p>
        <p>
          <strong>{t('websiteLabel')}</strong>{' '}
          <a
            href="https://www.mydearteachermovement.org"
            target="_blank"
            rel="noreferrer"
          >
            mydearteachermovement.org
          </a>
        </p>
      </div>
    </div>
  );
};

export default Contact;
