import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';

i18n
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    fallbackLng: 'en',
    debug: true,
    interpolation: {
      escapeValue: false,
    },
    resources: {
      en: {
        translation: {
          missionTitle: "WHY WE EXIST",
          missionSub: "Empowering Teachers. Uplifting Learners. Celebrating Neurodiversity.",
          inclusiveEducationTitle: "Inclusive Education",
          inclusiveEducationDesc: "Every child belongs. We build learning spaces that welcome all abilities, backgrounds, and needs.",
          teacherEmpowermentTitle: "Teacher Empowerment",
          teacherEmpowermentDesc: "We equip educators with practical tools, confidence, and community to champion inclusive classrooms.",
          neurodiversityTitle: "Neurodiversity Celebration",
          neurodiversityDesc: "We spotlight the strengths in ADHD, autism, dyslexia, and more — and rewrite the narrative around difference.",
          policyTitle: "Advocacy & Policy Change",
          policyDesc: "We advocate for equitable policies that ensure access, dignity, and meaningful learning for every student.",
          joinMovement: "Join the Movement",
          exploreToolkit: "Explore Our Toolkit"
        }
      },
      ar: {
        translation: {
          missionTitle: "لماذا نحن موجودون",
          missionSub: "تمكين المعلمين. رفع مكانة المتعلمين. الاحتفال بالتنوع العصبي.",
          inclusiveEducationTitle: "التعليم الشامل",
          inclusiveEducationDesc: "كل طفل يستحق الانتماء. نحن نبني بيئات تعلم ترحب بجميع القدرات والخلفيات والاحتياجات.",
          teacherEmpowermentTitle: "تمكين المعلمين",
          teacherEmpowermentDesc: "نقدم للمعلمين أدوات عملية وثقة ودعم لبناء فصول دراسية شاملة.",
          neurodiversityTitle: "الاحتفال بالتنوع العصبي",
          neurodiversityDesc: "نُبرز نقاط القوة في التوحد، ADHD، عسر القراءة والمزيد — ونعيد كتابة الرواية حول الاختلاف.",
          policyTitle: "المناصرة وتغيير السياسات",
          policyDesc: "نُناصر السياسات التي تضمن الإنصاف والكرامة والتعليم الهادف لكل طالب.",
          joinMovement: "انضم إلى الحركة",
          exploreToolkit: "استكشف أدواتنا"
        }
      }
    }
  });

export default i18n;
