import React, { useState, useEffect } from 'react';

const heroImages = [
  '/images/slide1.jpg',
  '/images/slide2.jpg',
  '/images/slide3.jpg'
];

const galleryImages = [
  '/images/photo1.jpg',
  '/images/photo2.jpg',
  '/images/photo3.jpg',
  '/images/photo4.jpg',
  '/images/photo5.jpg',
  '/images/photo6.jpg'
];

const Home = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [showScroll, setShowScroll] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % heroImages.length);
    }, 4000);

    const handleScroll = () => setShowScroll(window.scrollY > 300);

    window.addEventListener('scroll', handleScroll);
    return () => {
      clearInterval(interval);
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const scrollToTop = () => window.scrollTo({ top: 0, behavior: 'smooth' });

  return (
    <>
      {/* Logo Header */}
      <div style={{ textAlign: 'center', padding: '2rem' }}>
        <img
          src="/images/dearteacher-logo.png"
          alt="MyDearTeacher Logo"
          style={{ maxWidth: '220px', height: 'auto' }}
        />
      </div>

      {/* Hero Slider */}
      <div style={{ position: 'relative', height: '65vh', overflow: 'hidden' }}>
        <img
          src={heroImages[currentSlide]}
          alt="hero slide"
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            transition: 'opacity 1s ease-in-out'
          }}
        />
      </div>

      {/* Welcome Text */}
      <div style={{
        backgroundColor: '#FFF4EC',
        padding: '4rem 2rem',
        textAlign: 'center',
        borderTop: '8px solid #F4888C',
        borderBottom: '4px solid #F4888C'
      }}>
        <h1 style={{ fontSize: '2.5rem', color: '#F4888C', marginBottom: '1rem' }}>
          Reimagining Education for Every Child
        </h1>
        <p style={{
          fontSize: '1.2rem',
          maxWidth: '800px',
          margin: '0 auto',
          color: '#4b5563',
          lineHeight: '1.8'
        }}>
          Because no one is broken. <br />
          Join us in building classrooms that see every child â€” not as a problem to fix,
          but as a story to understand.
        </p>
      </div>

      {/* Gallery */}
      <div className="container">
        <h2 style={{ textAlign: 'center' }}>ðŸ“¸ Voices & Moments from the Movement</h2>
        <div className="gallery-grid">
          {galleryImages.map((src, idx) => (
            <img key={idx} src={src} alt={`Gallery ${idx + 1}`} />
          ))}
        </div>
      </div>

      {/* Scroll to Top */}
      {showScroll && (
        <button
          className="scroll-top"
          onClick={scrollToTop}
          aria-label="Scroll to top"
        >
          â†‘
        </button>
      )}
    </>
  );
};

export default Home;
