import React, { useState, useEffect } from 'react';
import './App.css'; // make sure gallery-horizontal styles are here

const heroImages = [
  '/images/slide1.jpg',
  '/images/slide2.jpg',
  '/images/slide3.jpg',
  '/images/slide4.jpg',
  '/images/slide5.jpg',
  '/images/slide6.jpg',
];

const rotatingTaglines = [
  '1 in 5 students is neurodivergent.',
  'Inclusion is not extra — it’s essential.',
  'Every child deserves to feel seen.',
];

const galleryImages = [
  '/images/photo1.jpg',
  '/images/photo2.jpg',
  '/images/photo3.jpg',
  '/images/photo4.jpg',
];

const Home = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [currentTagline, setCurrentTagline] = useState(0);
  const [showScroll, setShowScroll] = useState(false);

  /* ---------- Effects ---------- */
  useEffect(() => {
    const slideInterval = setInterval(
      () => setCurrentSlide((prev) => (prev + 1) % heroImages.length),
      4000
    );

    const tagInterval = setInterval(
      () => setCurrentTagline((prev) => (prev + 1) % rotatingTaglines.length),
      3000
    );

    const handleScroll = () => setShowScroll(window.scrollY > 300);
    window.addEventListener('scroll', handleScroll);

    return () => {
      clearInterval(slideInterval);
      clearInterval(tagInterval);
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const scrollToTop = () => window.scrollTo({ top: 0, behavior: 'smooth' });

  return (
    <>
      {/* Hero Section */}
      <div style={{ position: 'relative', height: '48vh', overflow: 'hidden' }}>
        <img
          src={heroImages[currentSlide]}
          alt={`Hero ${currentSlide + 1}`}
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            transition: 'opacity 1s ease-in-out',
          }}
        />
        <div
          style={{
            position: 'absolute',
            bottom: '2rem',
            left: '50%',
            transform: 'translateX(-50%)',
            backgroundColor: 'rgba(255,244,236,0.9)',
            padding: '1rem 2rem',
            borderRadius: '12px',
            textAlign: 'center',
            boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
          }}
        >
          <h2
            className="storybook-heading"
            style={{ color: '#F4888C', marginBottom: '0.5rem' }}
          >
            {rotatingTaglines[currentTagline]}
          </h2>
          <div style={{ marginTop: '0.5rem' }}>
            <a href="/books" className="btn" style={{ marginRight: '1rem' }}>
              Explore the Toolkit
            </a>
            <a href="/about" className="btn" style={{ marginRight: '1rem' }}>
              Meet the Author
            </a>
            <a
              href="#"
              className="btn"
              style={{ backgroundColor: '#ccc' }}
              onClick={(e) => e.preventDefault()}
            >
              Watch Our Movement Video
            </a>
          </div>
        </div>
      </div>

      {/* Welcome Message */}
      <div
        style={{
          backgroundColor: '#FFF4EC',
          padding: '3rem 1.5rem',
          textAlign: 'center',
          borderTop: '8px solid #F4888C',
          borderBottom: '4px solid #F4888C',
        }}
      >
        <h1
          style={{ fontSize: '2.5rem', color: '#F4888C', marginBottom: '1rem' }}
        >
          Reimagining Education for Every Child
        </h1>
        <p
          style={{
            fontSize: '1.2rem',
            maxWidth: '800px',
            margin: '0 auto',
            color: '#4b5563',
            lineHeight: '1.8',
          }}
        >
          Because no one is broken.
          <br />
          Join us in building classrooms that see every child — not as a problem
          to fix, but as a story to understand.
        </p>
      </div>

      {/* Horizontal Gallery */}
      <div className="container">
        <h2 style={{ textAlign: 'center' }}>
          📸 Voices &amp; Moments from the Movement
        </h2>
        <div className="gallery-horizontal">
          {galleryImages.map((src, idx) => (
            <img
              key={idx}
              src={src}
              alt={`Moment ${idx + 1}`}
              className="gallery-img"
            />
          ))}
        </div>
      </div>

      {/* WhatsApp Icon (now above videos) */}
      <div className="container">
        <div
          className="whatsapp-icon"
          style={{
            display: 'flex',
            justifyContent: 'flex-end',
            marginTop: '5rem',
            marginBottom: '2rem',
          }}
        >
          <a
            href="https://wa.me/971505816347"
            target="_blank"
            rel="noreferrer"
            title="Chat with us on WhatsApp"
          >
            <img
              src="/images/whatsapp-icon.png"
              alt="WhatsApp Icon"
              width="60"
              height="60"
            />
          </a>
        </div>
      </div>

      {/* YouTube Section – LAST before footer */}
      <div
        className="container"
        style={{ textAlign: 'center', marginTop: '4rem' }}
      >
        <h2 style={{ color: '#F4888C' }}>🎥 Watch Our Videos</h2>
        <p
          style={{
            maxWidth: '700px',
            margin: '0 auto 2rem',
            color: '#4b5563',
          }}
        >
          Explore our message through powerful visual stories and educator
          insights.
        </p>
        <div
          style={{
            position: 'relative',
            paddingBottom: '56.25%',
            height: 0,
            overflow: 'hidden',
            maxWidth: '100%',
          }}
        >
          <iframe
            title="MyDearTeacher on YouTube"
            src="https://www.youtube.com/embed?listType=user_uploads&list=mydearteachermovement"
            frameBorder="0"
            allowFullScreen
            style={{
              position: 'absolute',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              borderRadius: '16px',
            }}
          ></iframe>
        </div>
      </div>

      {/* Scroll to Top */}
      {showScroll && (
        <button
          className="scroll-top"
          onClick={scrollToTop}
          aria-label="Scroll to top"
        >
          ↑
        </button>
      )}
    </>
  );
};

export default Home;
