import React from 'react';
import { Link, NavLink } from 'react-router-dom';
import { useTranslation } from 'react-i18next';

const Navbar = () => {
  const { t, i18n } = useTranslation();

  const changeLanguage = (lng) => i18n.changeLanguage(lng);

  return (
    <nav>
      {/* Logo */}
      <Link
        to="/"
        className="logo-link"
        style={{ display: 'flex', alignItems: 'center', textDecoration: 'none' }}
      >
        <img
          src="/images/dearteacher-logo.png"
          alt="MyDearTeacher Logo"
          className="nav-logo"
        />
        <span
          style={{
            fontWeight: 'bold',
            color: 'white',
            marginLeft: '0.5rem',
            fontSize: '1.1rem',
          }}
        >
          #MyDearTeacher
        </span>
      </Link>

      {/* Main Links */}
      <ul>
        <li>
          <NavLink to="/" end>{t('navHome')}</NavLink>
        </li>
        <li>
          <NavLink to="/mission">{t('navMission')}</NavLink>
        </li>
        <li>
          <NavLink to="/about">{t('navAbout')}</NavLink>
        </li>
        <li>
          <NavLink to="/books">{t('navBooks')}</NavLink>
        </li>
        <li>
          <NavLink to="/join">{t('navJoin')}</NavLink>
        </li>
        <li>
          <NavLink to="/contact">{t('navContact')}</NavLink>
        </li>
        <li>
          <NavLink to="/blog">{t('navBlog')}</NavLink>
        </li>

        {/* Language Switcher */}
        <li style={{ marginLeft: '1rem' }}>
          <button
            style={{ background: 'transparent', border: 'none', color: '#fff', cursor: 'pointer', fontWeight: '600' }}
            onClick={() => changeLanguage('en')}
            aria-label="Switch to English"
          >
            EN
          </button>
          <span style={{ color: '#fff' }}>|</span>
          <button
            style={{ background: 'transparent', border: 'none', color: '#fff', cursor: 'pointer', fontWeight: '600' }}
            onClick={() => changeLanguage('ar')}
            aria-label="التبديل إلى العربية"
          >
            ع
          </button>
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
