import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import '../App.css'; // Or './Mission.css' if it's a separate file

const cardVariants = {
  hidden: { opacity: 0, y: 30 },
  visible: (i) => ({
    opacity: 1,
    y: 0,
    transition: {
      delay: i * 0.2,
      duration: 0.6,
      ease: 'easeOut',
    },
  }),
};

const Mission = () => {
  return (
    <div className="container mission-wrapper">
      {/* Title with Background Image */}
      <div
        className="mission-header"
        style={{
          backgroundImage: 'url(/images/mission-bg.jpg)',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          padding: '4rem 1.5rem',
          textAlign: 'center',
          borderRadius: '16px',
          color: '#fff',
        }}
      >
      <h1 className="section-heading">WHY WE EXIST</h1>
        <p className="section-sub" style={{ maxWidth: '700px', margin: '0 auto' }}>
          Empowering Teachers. Uplifting Learners. Celebrating Neurodiversity.
        </p>
      </div>

      {/* Cards Grid */}
      <div className="mission-grid">
        {[
          {
            icon: 'ðŸ¤',
            title: 'Inclusive Education',
            desc:
              'We believe that inclusion is a right, not a privilege. Our mission is to create learning environments that embrace all abilities and support all students.',
          },
          {
            icon: 'ðŸŽ“',
            title: 'Teacher Empowerment',
            desc:
              'Teachers are the heart of inclusion. We offer tools and training to help them support diverse learners with confidence.',
          },
          {
            icon: 'ðŸ§©',
            title: 'Neurodiversity Awareness',
            desc:
              'We raise awareness about ADHD, autism, dyslexia and more, promoting strength-based and affirming approaches.',
          },
          {
            icon: 'ðŸ“¢',
            title: 'Policy & Advocacy',
            desc:
              'We advocate for inclusive policies and systemic reforms to create accessible, affirming classrooms for all.',
          },
        ].map((item, i) => (
          <motion.div
            key={i}
            className="mission-card"
            custom={i}
            initial="hidden"
            animate="visible"
            variants={cardVariants}
          >
            <div className="mission-icon" style={{ fontSize: '2rem', transition: 'transform 0.3s' }}>
              {item.icon}
            </div>
            <h3>{item.title}</h3>
            <p className="mission-desc">{item.desc}</p>
          </motion.div>
        ))}
      </div>

      {/* CTA Buttons */}
      <div style={{ textAlign: 'center', marginTop: '3rem' }}>
        <Link to="/join" className="btn">Join the Movement</Link>
        <Link to="/books" className="btn">Explore Our Toolkit</Link>
      </div>
    </div>
  );
};

export default Mission;
