// src/pages/Home.js
import React, { useState, useEffect } from 'react';

const images = [
  '/images/slide1.jpg',
  '/images/slide2.jpg',
  '/images/slide3.jpg'
];

const Home = () => {
  const [current, setCurrent] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrent((prev) => (prev + 1) % images.length);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  return (
    <>
      <div style={{ position: 'relative', height: '70vh', overflow: 'hidden' }}>
        <img
          src={images[current]}
          alt="Slide"
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            transition: 'opacity 1s ease-in-out'
          }}
        />
        <div style={{
          position: 'absolute',
          bottom: '2rem',
          left: '50%',
          transform: 'translateX(-50%)',
          color: 'white',
          background: 'rgba(0, 0, 0, 0.6)',
          padding: '2rem',
          borderRadius: '12px',
          maxWidth: '80%',
          textAlign: 'center'
        }}>
          <h1 style={{ fontSize: '2.5rem', marginBottom: '1rem' }}>Welcome to the #MyDearTeacher Movement</h1>
          <p style={{ fontSize: '1.2rem' }}>
            Reimagining education for every child. Because no one is broken.<br />
            Join us in building classrooms that see every child ‚Äî not as a problem to fix, but as a story to understand.
          </p>
        </div>
      </div>

      <div className="container">
        <h2>üåç Our Vision</h2>
        <p>A future where every learner feels safe, seen, and supported ‚Äî no matter how they learn or express themselves.</p>

        <h2>üìö Our Books</h2>
        <ul>
          <li>Neurodiverse Classrooms: A Teacher‚Äôs Guide to SEN Support</li>
          <li>Beyond Gifted: Supporting Talented and Twice-Exceptional Learners</li>
          <li>Dear Teacher, I Am Not Broken</li>
          <li>#MyDearTeacher Toolkit</li>
        </ul>

        <h2>üì£ Get Involved</h2>
        <p>From global campaigns to school training programs, we‚Äôre equipping educators and empowering voices.</p>
        <a href="/join" style={{
          display: 'inline-block',
          marginTop: '1rem',
          padding: '0.75rem 1.5rem',
          backgroundColor: '#f78ca2',
          color: 'white',
          borderRadius: '4px',
          textDecoration: 'none',
          fontWeight: '600'
        }}>Join the Movement</a>
      </div>
    </>
  );
};

export default Home;
