// src/i18n.js
import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';

i18n
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    fallbackLng: 'en',
    debug: true,
    detection: {
      order: ['localStorage', 'navigator'],
      caches: ['localStorage'],
    },
    interpolation: {
      escapeValue: false,
    },
    resources: {
      en: {
        translation: {
          missionTitle: "WHY WE EXIST",
          missionSub: "Empowering Teachers. Uplifting Learners. Celebrating Neurodiversity.",
          inclusiveEducationTitle: "Inclusive Education",
          inclusiveEducationDesc: "We believe that inclusion is a right, not a privilege. Our mission is to create learning environments that embrace all abilities and support all students.",
          teacherEmpowermentTitle: "Teacher Empowerment",
          teacherEmpowermentDesc: "Teachers are the heart of inclusion. We offer tools and training to help them support diverse learners with confidence.",
          neurodiversityAwarenessTitle: "Neurodiversity Awareness",
          neurodiversityAwarenessDesc: "We raise awareness about ADHD, autism, dyslexia and more, promoting strength-based and affirming approaches.",
          policyAdvocacyTitle: "Policy & Advocacy",
          policyAdvocacyDesc: "We advocate for inclusive policies and systemic reforms to create accessible, affirming classrooms for all."
        }
      },
      ar: {
        translation: {
          missionTitle: "لماذا نحن موجودون",
          missionSub: "تمكين المعلمين. رفع مكانة المتعلمين. الاحتفال بالتنوع العصبي.",
          inclusiveEducationTitle: "التعليم الشامل",
          inclusiveEducationDesc: "نعتقد أن الشمولية حق وليس امتيازًا. مهمتنا هي إنشاء بيئات تعليمية تحتضن جميع القدرات وتدعم جميع الطلاب.",
          teacherEmpowermentTitle: "تمكين المعلمين",
          teacherEmpowermentDesc: "المعلمون هم قلب الشمول. نقدم أدوات وتدريبًا لمساعدتهم على دعم المتعلمين المتنوعين بثقة.",
          neurodiversityAwarenessTitle: "التوعية بالتنوع العصبي",
          neurodiversityAwarenessDesc: "نرفع الوعي حول ADHD والتوحد وعسر القراءة والمزيد، ونعزز النهج القائم على نقاط القوة.",
          policyAdvocacyTitle: "السياسات والمناصرة",
          policyAdvocacyDesc: "ندافع عن سياسات شاملة وإصلاحات منهجية لإنشاء فصول دراسية يمكن الوصول إليها وتأكيدها للجميع."
        }
      }
    }
  });

export default i18n;
