import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import '../App.css';

const cardVariants = {
  hidden: { opacity: 0, y: 30 },
  visible: (i) => ({
    opacity: 1,
    y: 0,
    transition: {
      delay: i * 0.2,
      duration: 0.6,
      ease: 'easeOut',
    },
  }),
};

const Mission = () => {
  const cards = [
    {
      icon: 'ü§ù',
      title: 'Inclusive Education',
      desc:
        'We believe that inclusion is a right, not a privilege. Our mission is to create learning environments that embrace all abilities and support all students.',
    },
    {
      icon: 'üéì',
      title: 'Teacher Empowerment',
      desc:
        'Teachers are the heart of inclusion. We offer tools and training to help them support diverse learners with confidence.',
    },
    {
      icon: 'üß©',
      title: 'Neurodiversity Awareness',
      desc:
        'We raise awareness about ADHD, autism, dyslexia and more, promoting strength-based and affirming approaches.',
    },
    {
      icon: 'üì¢',
      title: 'Policy & Advocacy',
      desc:
        'We advocate for inclusive policies and systemic reforms to create accessible, affirming classrooms for all.',
    },
  ];

  return (
    <div className="container mission-wrapper">
      {/* Header with Background */}
      <div
        className="mission-header"
        style={{
          backgroundImage: 'url(/images/mission-bg.jpg)',
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          padding: '6rem 1.5rem',
          textAlign: 'center',
          borderRadius: '16px',
          color: '#fff',
        }}
      >
        <h1 className="section-heading" style={{ fontSize: '3rem', fontWeight: 'bold' }}>
          WHY WE EXIST
        </h1>
        <p className="section-sub" style={{ maxWidth: '720px', margin: '1rem auto', fontSize: '1.25rem' }}>
          Empowering Teachers. Uplifting Learners. Celebrating Neurodiversity.
        </p>
      </div>

      {/* Cards Section */}
      <div className="mission-grid">
        {cards.map((item, i) => (
          <motion.div
            key={i}
            className="mission-card"
            custom={i}
            initial="hidden"
            animate="visible"
            variants={cardVariants}
            whileHover={{ scale: 1.05 }}
          >
            <div
              className="mission-icon"
              style={{
                fontSize: '2.5rem',
                marginBottom: '1rem',
                transition: 'transform 0.3s',
              }}
            >
              {item.icon}
            </div>
            <h3 style={{ marginBottom: '0.5rem' }}>{item.title}</h3>
            <p className="mission-desc">{item.desc}</p>
          </motion.div>
        ))}
      </div>

      {/* CTA Buttons */}
      <div style={{ textAlign: 'center', marginTop: '3rem' }}>
        <Link to="/join" className="btn" style={{ marginRight: '1rem' }}>
          Join the Movement
        </Link>
        <Link to="/books" className="btn">
          Explore Our Toolkit
        </Link>
      </div>
    </div>
  );
};

export default Mission;
